import React, { useState } from 'react';
import axios from 'axios';

const Quiz = () => {
  const [inputText, setInputText] = useState('');
  const [quiz, setQuiz] = useState('');
  const [loading, setLoading] = useState(false);

  const generateQuiz = async () => {
    try {
      setLoading(true);
      const res = await axios.post('http://localhost:8080/quiz', {
        inputText,
      });
      setQuiz(res.data);
    } catch (err) {
      console.error('Error generating quiz:', err);
      setQuiz('Failed to generate quiz.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">Quiz Generator</h2>
      <textarea
        className="w-full p-2 border rounded"
        rows={5}
        placeholder="Enter text or paste extracted content..."
        value={inputText}
        onChange={(e) => setInputText(e.target.value)}
      />
      <button
        className="mt-3 px-4 py-2 bg-green-600 text-white rounded"
        onClick={generateQuiz}
        disabled={loading}
      >
        {loading ? 'Generating...' : 'Generate Quiz'}
      </button>

      {quiz && (
        <div className="mt-4 whitespace-pre-wrap p-3 border bg-gray-50 rounded">
          <strong>Generated Quiz:</strong>
          <div>{quiz}</div>
        </div>
      )}
    </div>
  );
};

export default Quiz;
