import React, { useState } from 'react';
import axios from 'axios';

const PdfUploader = ({ onUploadSuccess }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadMessage, setUploadMessage] = useState('');

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert("Please select a PDF file first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      setUploading(true);
      const response = await axios.post("http://localhost:8080/upload", formData);
      setUploadMessage("Uploaded: " + selectedFile.name);

      // Inform parent component that upload succeeded
      onUploadSuccess && onUploadSuccess(selectedFile.name);
    } catch (error) {
      console.error("Upload failed:", error);
      setUploadMessage("Upload failed.");
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="p-4 border rounded shadow mb-4">
      <h2 className="text-lg font-semibold mb-2">Upload PDF</h2>
      <input type="file" accept="application/pdf" onChange={handleFileChange} />
      <button
        onClick={handleUpload}
        className="ml-2 px-4 py-1 bg-blue-600 text-white rounded"
        disabled={uploading}
      >
        {uploading ? "Uploading..." : "Upload"}
      </button>
      {uploadMessage && <p className="mt-2">{uploadMessage}</p>}
    </div>
  );
};

export default PdfUploader;
