import React, { useState } from 'react';
import axios from 'axios';

const Flashcards = () => {
  const [file, setFile] = useState(null);
  const [filename, setFilename] = useState('');
  const [flashcards, setFlashcards] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return alert("Please select a file.");

    const formData = new FormData();
    formData.append('file', file);

    try {
      setLoading(true);
      // Upload the file
      const res = await axios.post('http://localhost:8080/upload', formData);
      setFilename(file.name);
      alert("File uploaded. Now generating flashcards...");
    } catch (err) {
      console.error(err);
      alert("Upload failed");
    } finally {
      setLoading(false);
    }
  };

  const generateFlashcards = async () => {
    if (!filename) return alert("Upload a file first.");

    try {
      setLoading(true);
      const res = await axios.get(`http://localhost:8080/flashcards?filename=${filename}`);
      setFlashcards(res.data);
    } catch (err) {
      console.error(err);
      alert("Failed to generate flashcards");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Flashcard Generator (PDF)</h2>

      <input type="file" accept="application/pdf" onChange={handleFileChange} className="mb-2" />
      <div className="flex gap-2 mb-4">
        <button onClick={handleUpload} className="px-4 py-2 bg-blue-500 text-white rounded">Upload</button>
        <button onClick={generateFlashcards} className="px-4 py-2 bg-green-500 text-white rounded">Generate Flashcards</button>
      </div>

      {loading && <p>Processing...</p>}

      {flashcards.length > 0 && (
        <div className="mt-6">
          <h3 className="text-xl font-semibold mb-2">Generated Flashcards</h3>
          <ul className="space-y-4">
            {flashcards.map((card, idx) => (
              <li key={idx} className="p-3 border rounded shadow">
                <p><strong>Q:</strong> {card.question}</p>
                <p><strong>A:</strong> {card.answer}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Flashcards;
