import React, { useState } from 'react';
import axios from 'axios';

const Summarizer = () => {
  const [inputText, setInputText] = useState('');
  const [summary, setSummary] = useState('');
  const [customPrompt, setCustomPrompt] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSummarize = async () => {
    if (!inputText.trim()) {
      alert('Please enter some text.');
      return;
    }

    setLoading(true);
    try {
      const res = await axios.post('http://localhost:8080/summarize', {
        inputText: customPrompt ? `${customPrompt}\n\n${inputText}` : inputText,
      });
      setSummary(res.data);
    } catch (error) {
      console.error('Error summarizing:', error);
      alert('Failed to summarize');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-4 border rounded shadow">
      <h2 className="text-xl font-bold mb-2">Summarizer</h2>
      <textarea
        placeholder="Paste your text here..."
        value={inputText}
        onChange={(e) => setInputText(e.target.value)}
        rows={6}
        className="w-full p-2 border mb-2"
      />
      <input
        type="text"
        placeholder="Optional: Custom prompt"
        value={customPrompt}
        onChange={(e) => setCustomPrompt(e.target.value)}
        className="w-full p-2 border mb-2"
      />
      <button
        onClick={handleSummarize}
        className="px-4 py-2 bg-blue-500 text-white rounded"
      >
        Summarize
      </button>

      {loading && <p className="mt-2">Summarizing...</p>}

      {summary && (
        <div className="mt-4 p-3 bg-gray-100 border rounded">
          <h3 className="font-semibold mb-1">Summary:</h3>
          <p>{summary}</p>
        </div>
      )}
    </div>
  );
};

export default Summarizer;
