import React, { useState } from 'react';
import Summarizer from './components/Summarizer';
import Flashcards from './components/Flashcards';
import Quiz from './components/Quiz'; // ðŸ‘ˆ New

const App = () => {
  const [activeTab, setActiveTab] = useState('summarizer');

  return (
    <div className="p-6 font-sans max-w-3xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">AskEd AI Assistant</h1>

      <div className="flex gap-4 mb-4">
        <button
          className={`px-4 py-2 rounded ${
            activeTab === 'summarizer' ? 'bg-blue-500 text-white' : 'bg-gray-200'
          }`}
          onClick={() => setActiveTab('summarizer')}
        >
          Summarizer
        </button>
        <button
          className={`px-4 py-2 rounded ${
            activeTab === 'flashcards' ? 'bg-blue-500 text-white' : 'bg-gray-200'
          }`}
          onClick={() => setActiveTab('flashcards')}
        >
          Flashcards
        </button>
        <button
          className={`px-4 py-2 rounded ${
            activeTab === 'quiz' ? 'bg-blue-500 text-white' : 'bg-gray-200'
          }`}
          onClick={() => setActiveTab('quiz')}
        >
          Quiz
        </button>
      </div>

      <div className="mt-6">
        {activeTab === 'summarizer' && <Summarizer />}
        {activeTab === 'flashcards' && <Flashcards />}
        {activeTab === 'quiz' && <Quiz />} {/* ðŸ‘ˆ New */}
      </div>
    </div>
  );
};

export default App;
