import React, { useState } from 'react';
import axios from 'axios';

const Flashcards = () => {
  const [filename, setFilename] = useState('');
  const [flashcards, setFlashcards] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleFetchFlashcards = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`http://localhost:8080/flashcards`, {
        params: { filename },
      });
      const data = res.data;

      if (Array.isArray(data)) {
        setFlashcards(data);
      } else {
        setFlashcards(JSON.parse(data)); // fallback if returned as string
      }

      setError('');
    } catch (err) {
      console.error(err);
      setError('Failed to fetch flashcards');
    }
    setLoading(false);
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-2">Generate Flashcards</h2>
      <input
        type="text"
        placeholder="Enter filename (e.g. file.pdf)"
        value={filename}
        onChange={(e) => setFilename(e.target.value)}
        className="p-2 border rounded mb-2 w-full"
      />
      <button
        onClick={handleFetchFlashcards}
        className="bg-blue-500 text-white px-4 py-2 rounded"
      >
        {loading ? 'Loading...' : 'Fetch Flashcards'}
      </button>

      {error && <p className="text-red-500 mt-2">{error}</p>}

      {flashcards.length > 0 && (
        <div className="mt-4">
          <h3 className="font-semibold mb-2">Flashcards:</h3>
          {flashcards.map((card, index) => (
            <div key={index} className="border p-3 mb-2 rounded bg-gray-100">
              <p><strong>Q:</strong> {card.question}</p>
              <p><strong>A:</strong> {card.answer}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Flashcards;
