import React, { useState } from 'react';
import PdfUploader from './components/PdfUploader';
import Summarizer from './components/Summarizer';

const App = () => {
  const [extractedText, setExtractedText] = useState('');
  const [filename, setFilename] = useState('');

  const handleUploadSuccess = async (uploadedFilename) => {
    setFilename(uploadedFilename);

    try {
      const res = await fetch(`http://localhost:8080/extract?filename=${uploadedFilename}`);
      const data = await res.text();
      const textOnly = data.replace(/^Extracted text:\s*/, '');
      setExtractedText(textOnly);
    } catch (error) {
      console.error('Failed to extract:', error);
    }
  };

  return (
    <div className="p-8 max-w-3xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">AskEd</h1>
      <PdfUploader onUploadSuccess={handleUploadSuccess} />
      {extractedText && <Summarizer text={extractedText} />}
    </div>
  );
};

export default App;
